// Create player object
var player = {
  x: 0,
  y: 0,
  direction: 1,
  bulletSpeed: 3,
};

//Create an array of projectiles
var projectiles = [];

// a projectile need to have an x and y pos, a speed, and a direction

function Projectile(x, y, speed, direction) {
  this.x = x;
  this.y = y;
  this.speed = speed;
  this.direction = direction;
}

function controllers() {
  // if btn(0) is pressed, create a new projectile
  if (btnp(5)) {
    projectiles.push(
      new Projectile(player.x, player.y, player.bulletSpeed, player.direction)
    );
  }

  if (btn(3)) {
    player.x++;
    player.direction = 1;
    player.bulletSpeed = 3;
  }
  if (btn(2)) {
    player.x--;
    player.direction = -1;
    player.bulletSpeed = 3;
  }
  if (btn(1)) player.y++;
  if (btn(0)) player.y--;
}

function printAndMoveProjectile() {
  // for each projectile, print it on the screen and move it
  for (var i = 0; i < projectiles.length; i++) {
    print("*", projectiles[i].x, projectiles[i].y);
    projectiles[i].x += projectiles[i].speed * projectiles[i].direction;
  }
}

function printPlayerDirection() {
  print("Direction: " + player.direction, 10, 20);
  if (player.direction == -1) {
    print("<", 10, 30);
  } else {
    print(">", 10, 30);
  }
}

function printPlayer() {
  print("0", player.x, player.y);
}

function multiplyBullet() {
  // if a bullet hits a wall, reverse its direction
  for (var i = 0; i < projectiles.length; i++) {
    if (projectiles[i].x < 0 || projectiles[i].x > 190) {
      projectiles[i].direction *= -1;
    }
  }
}

function TIC() {
  cls(0);
  controllers();
  printAndMoveProjectile();
  multiplyBullet();
  printPlayerDirection();
  printPlayer();
}

